mapper.select:
SELECT * FROM ( SELECT BBS_SEQ , NM , EXPLN , REPLY_YN , COMENT_YN , ATCH_YN , SECRT_YN , SRT_ORD , REG_NO , REG_DT , UPD_NO , UPD_DT , STAT FROM TB_BBS WHERE 1=1
AND STAT = 1
AND STAT = 0
AND NM LIKE CONCAT('%', #{searchKeyword}, '%')
) AS T1
ORDER BY SRT_ORD ASC LIMIT #{skip}, #{amount}

mapper.insert:
INSERT INTO TB_BBS(
	NM
	, EXPLN
	, REPLY_YN
	, COMENT_YN
	, ATCH_YN
	, SECRT_YN
	, SRT_ORD
	, REG_NO
	, REG_DT
	, UPD_NO
	, UPD_DT
	, STAT
)VALUES (
	#{nm}
	, #{expln}
	, #{replyYn}
	, #{comentYn}
	, #{atchYn}
	, #{secrtYn}
	, (SELECT MAX(SRT_ORD) FROM TB_BBS)
	, #{regNo}
	, NOW()
	, #{regNo}
	, NOW()
	, 1
)

mapper.update:
UPDATE
	TB_BBS
SET
	NM 			= #{nm}
	, EXPLN 	= #{expln}
	, REPLY_YN 	= #{replyYn}
	, COMENT_YN = #{comentYn}
	, ATCH_YN 	= #{atchYn}
	, SECRT_YN 	= #{secrtYn}
	, UPD_NO 	= #{updNo}
	, UPD_DT 	= NOW()
WHERE 1=1
	AND BBS_SEQ = #{bbsSeq}

UPDATE
	TB_BBS
SET
	STAT = #{stat}
	, UPD_NO = #{updNo}
	, UPD_DT = NOW()
WHERE 1=1
	AND BBS_SEQ = #{bbsSeq}
