mapper.select:
SELECT * FROM ( SELECT CASE WHEN T1.LVL = 0 THEN @rownum := @rownum + 1 ELSE NULL END AS ROW_NUM , BOARD_SEQ , BBS_SEQ , TITLE , CONT , REF , STEP , LVL , PWD_YN , PWD , READ_CNT , REG_NO , REG_DT , UPD_NO , UPD_DT , STAT , (SELECT NM FROM TB_BBS WHERE BBS_SEQ = T1.BBS_SEQ) BBS_NM , (SELECT USER_NM FROM TB_USER WHERE USER_SEQ = T1.REG_NO) USER_NM , (SELECT COUNT(*) FROM TB_ATTACH WHERE BOARD_SEQ = T1.BOARD_SEQ) ATCH_CNT FROM TB_BOARD T1, (SELECT @rownum := 0) AS TMP WHERE 1=1
AND BBS_SEQ = #{bbsSeq}
AND STAT = 1
AND STAT = 0
AND TITLE LIKE CONCAT('%', #{searchKeyword}, '%') AND CONT LIKE CONCAT('%', #{searchKeyword}, '%') AND (SELECT USER_NM FROM TB_USER WHERE USER_SEQ = T1.REG_NO) LIKE CONCAT('%', #{searchKeyword}, '%')
AND ( TITLE LIKE CONCAT('%', #{searchKeyword}, '%') OR CONT LIKE CONCAT('%', #{searchKeyword}, '%') OR (SELECT USER_NM FROM TB_USER WHERE USER_SEQ = T1.REG_NO) LIKE CONCAT('%', #{searchKeyword}, '%') )
) AS SUB ORDER BY REF DESC, STEP LIMIT #{skip}, #{amount}

mapper.insert:
INSERT INTO TB_BOARD(
	BBS_SEQ
	, TITLE
	, CONT
	, REF
	, STEP
	, LVL
	, PWD_YN
	, PWD
	, READ_CNT
	, REG_NO
	, REG_DT
	, UPD_NO
	, UPD_DT
	, STAT
)VALUES (
	#{bbsSeq}
	, #{title}
	, #{cont}
	, #{ref}
	, #{step}
	, #{lvl}
	
		
			, #{pwdYn}
			, SHA2(#{pwd}, 256)
		
		
			, 'N'
			, #{pwd}
		
	
	, #{readCnt}
	, #{regNo}
	, NOW()
	, #{regNo}
	, NOW()
	, #{stat}
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE
	TB_BOARD
SET
	TITLE    = #{title}
	, CONT 	 = #{cont}
	
		, PWD_YN = #{pwdYn}
		, PWD	 = SHA2(#{pwd}, 256)
	
	
		, PWD_YN = #{pwdYn}
		, PWD	 = ''
	
	, UPD_NO = #{updNo}
	, UPD_DT = NOW()
	, STAT = #{stat}
WHERE 1=1
	AND BOARD_SEQ = #{boardSeq}

UPDATE
	TB_BOARD
SET
	STAT = #{stat}
	, UPD_NO = #{updNo}
	, UPD_DT = NOW()
WHERE 1=1
	AND BOARD_SEQ = #{boardSeq}

UPDATE
	TB_BOARD
SET
	STAT = #{stat}
	, UPD_NO = #{updNo}
	, UPD_DT = NOW()
WHERE 1=1
	AND REF = #{boardSeq}

UPDATE
	TB_BOARD
SET
	REF	= #{ref}
	, UPD_NO = #{updNo}
	, UPD_DT = NOW()
WHERE 1=1
	AND BOARD_SEQ = #{boardSeq}
