mapper.insert:
INSERT INTO TB_COMMENT(
	BOARD_SEQ
	, CN
	, REF
	, STEP
	, LVL
	, PWD
	, REG_NO
	, REG_DT
	, UPD_NO
	, UPD_DT
	, STAT
)VALUES (
	#{boardSeq}
	, #{cn}
	, #{ref}
	, #{step}
	, #{lvl}
	, #{pwd}
	, #{regNo}
	, NOW()
	, #{regNo}
	, NOW()
	, 1
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE 
	TB_COMMENT
SET
	CN = #{cn}
	, UPD_NO = #{updNo}
	, UPD_DT = NOW()
WHERE 1=1
	AND CMNT_SEQ = #{cmntSeq}
