mapper.insert:
INSERT INTO TB_MAIN(
	MAIN_SE
	, TOP_BN_NM
	, BOTM_BN_NM
	, TECH_NM
	, POFOR_NM
	, EXPLN
	, SRT_ORD
	, REG_NO
	, REG_DT
	, UPD_NO
	, UPD_DT
	, STAT
)VALUES (
	#{mainSe}
	, #{topBnNm}
	, #{botmBnNm}
	, #{techNm}
	, #{poforNm}
	, #{expln}

		, (SELECT MAX(SRT_ORD)+1 FROM (SELECT SRT_ORD FROM TB_MAIN WHERE MAIN_SE = 'T') AS TMP)
		, 0

	, #{regNo}
	, NOW()
	, #{regNo}
	, NOW()
	, 1
)

	SELECT LAST_INSERT_ID()

mapper.update:
UPDATE
	TB_MAIN A JOIN TB_MAIN B ON A.MAIN_SEQ = B.MAIN_SEQ
SET
    A.TOP_BN_NM  = #{topBnNm}
    , A.BOTM_BN_NM = #{botmBnNm}
    , A.TECH_NM    = #{techNm}
    , A.POFOR_NM   = #{poforNm}
    , A.EXPLN      = #{expln}
    , A.UPD_NO     = #{updNo}
    , A.UPD_DT	   =

		CASE WHEN B.TOP_BN_NM != #{topBnNm} OR B.BOTM_BN_NM != #{botmBnNm} THEN NOW() ELSE A.UPD_DT END
		CASE WHEN B.POFOR_NM = #{poforNm} THEN A.UPD_DT ELSE NOW() END
		CASE WHEN B.TECH_NM = #{techNm} THEN A.UPD_DT ELSE NOW() END

WHERE A.MAIN_SEQ = #{mainSeq}

UPDATE
	TB_MAIN
SET
	UPD_NO 		= #{updNo}
	, UPD_DT 	= NOW()
	, STAT 		= #{stat}
WHERE 1=1
	AND MAIN_SEQ = #{mainSeq}

mapper.delete:
DELETE FROM TB_MAIN
WHERE 1=1
	AND MAIN_SEQ IN(

 		#{seq}

	)
