mapper.insert:
INSERT INTO TB_USER(
	USER_ID
	, USER_PWD
	, USER_NM
	, USER_SE
	, STAT
)VALUES (
	#{userId}
	, SHA2(#{userPwd}, 256)
	, #{userNm}
	, #{userSe}
	, 1
)

mapper.update:
UPDATE TB_USER SET USER_NM = #{userNm}
, USER_PWD = SHA2(#{userPwd}, 256)
WHERE 1=1 AND USER_SEQ = #{userSeq}

UPDATE
	TB_USER
SET
	STAT = #{stat}
WHERE 1=1
	AND USER_SEQ = #{userSeq}
	AND USER_ID = #{userId}
